		  F@_1, TrUserData) ->
    skip_varint_Token(Rest, Z1, Z2, F@_1, TrUserData);
skip_varint_Token(<<0:1, _:7, Rest/binary>>, Z1, Z2,
		  F@_1, TrUserData) ->
    dfp_read_field_def_Token(Rest, Z1, Z2, F@_1,
			     TrUserData).

skip_length_delimited_Token(<<1:1, X:7, Rest/binary>>,
			    N, Acc, F@_1, TrUserData)
    when N < 57 ->
    skip_length_delimited_Token(Rest, N + 7, X bsl N + Acc,
				F@_1, TrUserData);
skip_length_delimited_Token(<<0:1, X:7, Rest/binary>>,
			    N, Acc, F@_1, TrUserData) ->
    Length = X bsl N + Acc,
    <<_:Length/binary, Rest2/binary>> = Rest,
    dfp_read_field_def_Token(Rest2, 0, 0, F@_1, TrUserData).

skip_group_Token(Bin, FNum, Z2, F@_1, TrUserData) ->
    {_, Rest} = read_group(Bin, FNum),
    dfp_read_field_def_Token(Rest, 0, Z2, F@_1, TrUserData).

skip_32_Token(<<_:32, Rest/binary>>, Z1, Z2, F@_1,
	      TrUserData) ->
    dfp_read_field_def_Token(Rest, Z1, Z2, F@_1,
			     TrUserData).

skip_64_Token(<<_:64, Rest/binary>>, Z1, Z2, F@_1,
	      TrUserData) ->
    dfp_read_field_def_Token(Rest, Z1, Z2, F@_1,
			     TrUserData).

decode_msg_UserID(Bin, TrUserData) ->
    dfp_read_field_def_UserID(Bin, 0, 0, id([], TrUserData),
			      TrUserData).

dfp_read_field_def_UserID(<<10, Rest/binary>>, Z1, Z2,
			  F@_1, TrUserData) ->
    d_field_UserID_value(Rest, Z1, Z2, F@_1, TrUserData);
dfp_read_field_def_UserID(<<>>, 0, 0, F@_1, _) ->
    #{value => F@_1};
dfp_read_field_def_UserID(Other, Z1, Z2, F@_1,
			  TrUserData) ->
    dg_read_field_def_UserID(Other, Z1, Z2, F@_1,
			     TrUserData).

dg_read_field_def_UserID(<<1:1, X:7, Rest/binary>>, N,
			 Acc, F@_1, TrUserData)
    when N < 32 - 7 ->
    dg_read_field_def_UserID(Rest, N + 7, X bsl N + Acc,
			     F@_1, TrUserData);
dg_read_field_def_UserID(<<0:1, X:7, Rest/binary>>, N,
			 Acc, F@_1, TrUserData) ->
    Key = X bsl N + Acc,
    case Key of
      10 ->
	  d_field_UserID_value(Rest, 0, 0, F@_1, TrUserData);
      _ ->
	  case Key band 7 of
	    0 -> skip_varint_UserID(Rest, 0, 0, F@_1, TrUserData);
	    1 -> skip_64_UserID(Rest, 0, 0, F@_1, TrUserData);
	    2 ->
		skip_length_delimited_UserID(Rest, 0, 0, F@_1,
					     TrUserData);
	    3 ->
		skip_group_UserID(Rest, Key bsr 3, 0, F@_1, TrUserData);
	    5 -> skip_32_UserID(Rest, 0, 0, F@_1, TrUserData)
	  end
    end;
dg_read_field_def_UserID(<<>>, 0, 0, F@_1, _) ->
    #{value => F@_1}.

d_field_UserID_value(<<1:1, X:7, Rest/binary>>, N, Acc,
		     F@_1, TrUserData)
    when N < 57 ->
    d_field_UserID_value(Rest, N + 7, X bsl N + Acc, F@_1,
			 TrUserData);
d_field_UserID_value(<<0:1, X:7, Rest/binary>>, N, Acc,
		     _, TrUserData) ->
    {NewFValue, RestF} = begin
			   Len = X bsl N + Acc,
			   <<Utf8:Len/binary, Rest2/binary>> = Rest,
			   {id(unicode:characters_to_list(Utf8, unicode),
			       TrUserData),
			    Rest2}
			 end,
    dfp_read_field_def_UserID(RestF, 0, 0, NewFValue,
			      TrUserData).

skip_varint_UserID(<<1:1, _:7, Rest/binary>>, Z1, Z2,
		   F@_1, TrUserData) ->
    skip_varint_UserID(Rest, Z1, Z2, F@_1, TrUserData);
skip_varint_UserID(<<0:1, _:7, Rest/binary>>, Z1, Z2,
		   F@_1, TrUserData) ->
    dfp_read_field_def_UserID(Rest, Z1, Z2, F@_1,
			      TrUserData).

skip_length_delimited_UserID(<<1:1, X:7, Rest/binary>>,
			     N, Acc, F@_1, TrUserData)
    when N < 57 ->
    skip_length_delimited_UserID(Rest, N + 7, X bsl N + Acc,
				 F@_1, TrUserData);
skip_length_delimited_UserID(<<0:1, X:7, Rest/binary>>,
			     N, Acc, F@_1, TrUserData) ->
    Length = X bsl N + Acc,
    <<_:Length/binary, Rest2/binary>> = Rest,
    dfp_read_field_def_UserID(Rest2, 0, 0, F@_1,
			      TrUserData).

skip_group_UserID(Bin, FNum, Z2, F@_1, TrUserData) ->
    {_, Rest} = read_group(Bin, FNum),
    dfp_read_field_def_UserID(Rest, 0, Z2, F@_1,
			      TrUserData).

skip_32_UserID(<<_:32, Rest/binary>>, Z1, Z2, F@_1,
	       TrUserData) ->
    dfp_read_field_def_UserID(Rest, Z1, Z2, F@_1,
			      TrUserData).

skip_64_UserID(<<_:64, Rest/binary>>, Z1, Z2, F@_1,
	       TrUserData) ->
    dfp_read_field_def_UserID(Rest, Z1, Z2, F@_1,
			      TrUserData).

read_group(Bin, FieldNum) ->
    {NumBytes, EndTagLen} = read_gr_b(Bin, 0, 0, 0, 0, FieldNum),
    <<Group:NumBytes/binary, _:EndTagLen/binary, Rest/binary>> = Bin,
    {Group, Rest}.

%% Like skipping over fields, but record the total length,
%% Each field is <(FieldNum bsl 3) bor FieldType> ++ <FieldValue>
%% Record the length because varints may be non-optimally encoded.
%%
%% Groups can be nested, but assume the same FieldNum cannot be nested
%% because group field numbers are shared with the rest of the fields
%% numbers. Thus we can search just for an group-end with the same
%% field number.
%%
%% (The only time the same group field number could occur would
%% be in a nested sub message, but then it would be inside a
%% length-delimited entry, which we skip-read by length.)
read_gr_b(<<1:1, X:7, Tl/binary>>, N, Acc, NumBytes, TagLen, FieldNum)
  when N < (32-7) ->
    read_gr_b(Tl, N+7, X bsl N + Acc, NumBytes, TagLen+1, FieldNum);
read_gr_b(<<0:1, X:7, Tl/binary>>, N, Acc, NumBytes, TagLen,
          FieldNum) ->
    Key = X bsl N + Acc,
    TagLen1 = TagLen + 1,
    case {Key bsr 3, Key band 7} of
        {FieldNum, 4} -> % 4 = group_end
            {NumBytes, TagLen1};
        {_, 0} -> % 0 = varint
            read_gr_vi(Tl, 0, NumBytes + TagLen1, FieldNum);
        {_, 1} -> % 1 = bits64
            <<_:64, Tl2/binary>> = Tl,
            read_gr_b(Tl2, 0, 0, NumBytes + TagLen1 + 8, 0, FieldNum);
        {_, 2} -> % 2 = length_delimited
            read_gr_ld(Tl, 0, 0, NumBytes + TagLen1, FieldNum);
        {_, 3} -> % 3 = group_start
            read_gr_b(Tl, 0, 0, NumBytes + TagLen1, 0, FieldNum);
        {_, 4} -> % 4 = group_end
            read_gr_b(Tl, 0, 0, NumBytes + TagLen1, 0, FieldNum);
        {_, 5} -> % 5 = bits32
            <<_:32, Tl2/binary>> = Tl,
            read_gr_b(Tl2, 0, 0, NumBytes + TagLen1 + 4, 0, FieldNum)
    end.

read_gr_vi(<<1:1, _:7, Tl/binary>>, N, NumBytes, FieldNum)
  when N < (64-7) ->
    read_gr_vi(Tl, N+7, NumBytes+1, FieldNum);
read_gr_vi(<<0:1, _:7, Tl/binary>>, _, NumBytes, FieldNum) ->
    read_gr_b(Tl, 0, 0, NumBytes+1, 0, FieldNum).

read_gr_ld(<<1:1, X:7, Tl/binary>>, N, Acc, NumBytes, FieldNum)
  when N < (64-7) ->
    read_gr_ld(Tl, N+7, X bsl N + Acc, NumBytes+1, FieldNum);
read_gr_ld(<<0:1, X:7, Tl/binary>>, N, Acc, NumBytes, FieldNum) ->
    Len = X bsl N + Acc,
    NumBytes1 = NumBytes + 1,
    <<_:Len/binary, Tl2/binary>> = Tl,
    read_gr_b(Tl2, 0, 0, NumBytes1 + Len, 0, FieldNum).

merge_msgs(Prev, New, MsgName) when is_atom(MsgName) ->
    merge_msgs(Prev, New, MsgName, []).

merge_msgs(Prev, New, MsgName, Opts) ->
    TrUserData = proplists:get_value(user_data, Opts),
    case MsgName of
      'AccessReq' ->
	  merge_msg_AccessReq(Prev, New, TrUserData);
      'ThingID' -> merge_msg_ThingID(Prev, New, TrUserData);
      'Token' -> merge_msg_Token(Prev, New, TrUserData);
      'UserID' -> merge_msg_UserID(Prev, New, TrUserData)
    end.

-compile({nowarn_unused_function,merge_msg_AccessReq/3}).
merge_msg_AccessReq(PMsg, NMsg, _) ->
    S1 = #{},
    S2 = case {PMsg, NMsg} of
	   {_, #{token := NFtoken}} -> S1#{token => NFtoken};
	   {#{token := PFtoken}, _} -> S1#{token => PFtoken};
	   _ -> S1
	 end,
    case {PMsg, NMsg} of
      {_, #{chanID := NFchanID}} -> S2#{chanID => NFchanID};
      {#{chanID := PFchanID}, _} -> S2#{chanID => PFchanID};
      _ -> S2
    end.

-compile({nowarn_unused_function,merge_msg_ThingID/3}).
merge_msg_ThingID(PMsg, NMsg, _) ->
    S1 = #{},
    case {PMsg, NMsg} of
      {_, #{value := NFvalue}} -> S1#{value => NFvalue};
      {#{value := PFvalue}, _} -> S1#{value => PFvalue};
      _ -> S1
    end.

-compile({nowarn_unused_function,merge_msg_Token/3}).
merge_msg_Token(PMsg, NMsg, _) ->
    S1 = #{},
    case {PMsg, NMsg} of
      {_, #{value := NFvalue}} -> S1#{value => NFvalue};
      {#{value := PFvalue}, _} -> S1#{value => PFvalue};
      _ -> S1
    end.

-compile({nowarn_unused_function,merge_msg_UserID/3}).
merge_msg_UserID(PMsg, NMsg, _) ->
    S1 = #{},
    case {PMsg, NMsg} of
      {_, #{value := NFvalue}} -> S1#{value => NFvalue};
      {#{value := PFvalue}, _} -> S1#{value => PFvalue};
      _ -> S1
    end.


verify_msg(Msg, MsgName) when is_atom(MsgName) ->
    verify_msg(Msg, MsgName, []).

verify_msg(Msg, MsgName, Opts) ->
    TrUserData = proplists:get_value(user_data, Opts),
    case MsgName of
      'AccessReq' ->
	  v_msg_AccessReq(Msg, [MsgName], TrUserData);
      'ThingID' -> v_msg_ThingID(Msg, [MsgName], TrUserData);
      'Token' -> v_msg_Token(Msg, [MsgName], TrUserData);
      'UserID' -> v_msg_UserID(Msg, [MsgName], TrUserData);
      _ -> mk_type_error(not_a_known_message, Msg, [])
    end.


-compile({nowarn_unused_function,v_msg_AccessReq/3}).
-dialyzer({nowarn_function,v_msg_AccessReq/3}).
v_msg_AccessReq(#{} = M, Path, TrUserData) ->
    case M of
      #{token := F1} ->
	  v_type_string(F1, [token | Path], TrUserData);
      _ -> ok
    end,
    case M of
      #{chanID := F2} ->
	  v_type_uint64(F2, [chanID | Path], TrUserData);
      _ -> ok
    end,
    lists:foreach(fun (token) -> ok;
		      (chanID) -> ok;
		      (OtherKey) ->
			  mk_type_error({extraneous_key, OtherKey}, M, Path)
		  end,
		  maps:keys(M)),
    ok;
v_msg_AccessReq(M, Path, _TrUserData) when is_map(M) ->
    mk_type_error({missing_fields, [] -- maps:keys(M),
		   'AccessReq'},
		  M, Path);
v_msg_AccessReq(X, Path, _TrUserData) ->
    mk_type_error({expected_msg, 'AccessReq'}, X, Path).

-compile({nowarn_unused_function,v_msg_ThingID/3}).
-dialyzer({nowarn_function,v_msg_ThingID/3}).
v_msg_ThingID(#{} = M, Path, TrUserData) ->
    case M of
      #{value := F1} ->
	  v_type_uint64(F1, [value | Path], TrUserData);
      _ -> ok
    end,
    lists:foreach(fun (value) -> ok;
		      (OtherKey) ->
			  mk_type_error({extraneous_key, OtherKey}, M, Path)
		  end,
		  maps:keys(M)),
    ok;
v_msg_ThingID(M, Path, _TrUserData) when is_map(M) ->
