		   'ThingID'},
		  M, Path);
v_msg_ThingID(X, Path, _TrUserData) ->
    mk_type_error({expected_msg, 'ThingID'}, X, Path).

-compile({nowarn_unused_function,v_msg_Token/3}).
-dialyzer({nowarn_function,v_msg_Token/3}).
v_msg_Token(#{} = M, Path, TrUserData) ->
