-compile({nowarn_unused_function,v_msg_UserID/3}).
-dialyzer({nowarn_function,v_msg_UserID/3}).
v_msg_UserID(#{} = M, Path, TrUserData) ->
    case M of
      #{value := F1} ->
	  v_type_string(F1, [value | Path], TrUserData);
      _ -> ok
    end,
    lists:foreach(fun (value) -> ok;
		      (OtherKey) ->
			  mk_type_error({extraneous_key, OtherKey}, M, Path)
		  end,
